Properties.DashboardBody:
{
                        "widgets": [
                           {
                              "type":"metric",
                              "width": 6,
                              "height": 6,
                              "properties": {
                                    "metrics": [
                                        [
                                            {
                                            "expression": "SELECT AVG(CPUUtilization) FROM SCHEMA(\"AWS/EC2\", InstanceId) GROUP BY InstanceId",
                                            "id": "e1"
                                            }
                                        ]
                                    ],
                                    "period":300,
                                    "stat":"Average",
                                    "region":"us-east-1",
                                    "title":"Ghost EC2 CPU utilization",
                                    "yAxis": {
                                        "left": {
                                            "showUnits": false,
                                            "min": 0,
                                            "max": 100,
                                            "label": "%"
                                        }
                                    }
                                }
                           },
                           {
                              "type":"metric",
                              "width": 6,
                              "height": 6,
                              "properties": {
                                   "metrics": [
                                        [
                                            {
                                                "expression": "SELECT AVG(CPUUtilization) FROM SCHEMA(\"AWS/ECS\", ClusterName,ServiceName)",
                                                "id": "e2",
                                                "label": "ECS CPU Utilization"
                                            }
                                        ]
                                    ],
                                    "period": 300,
                                    "region": "us-east-1",
                                    "title": "Ghost ECS CPU utilization",
                                    "yAxis": {
                                        "left": {
                                            "showUnits": false,
                                            "min": 0,
                                            "max": 100,
                                            "label": "%"
                                        }
                                    }
                                } 
                            },
                            {
                                "type": "metric",
                                "width": 6,
                                "height": 6,
                                "properties": {
                                    "metrics": [
                                        [
                                        {
                                            "expression": "SELECT AVG(RunningTaskCount) FROM SCHEMA(\"ECS/ContainerInsights\", ClusterName, ServiceName) GROUP BY ClusterName, ServiceName ORDER BY AVG() DESC",
                                            "id": "e3",
                                            "label": "Task count"
                                        }
                                        ]
                                    ],
                                    "period": 300,
                                    "region": "us-east-1",
                                    "stat": "Average",
                                    "title": "ECS Task Count",
                                    "yAxis": {
                                        "left": {
                                            "showUnits": false,
                                            "min": 0,
                                            "label": "#"
                                        }
                                    }
                                }
                            },
                            {
                                "type": "metric",
                                "width": 6,
                                "height": 6,
                                "properties": {
                                    "metrics": [
                                        [
                                            {
                                                "expression": "SELECT SUM(ClientConnections) FROM \"AWS/EFS\"",
                                                "label": "Client Connections",
                                                "id": "e4"
                                            }
                                        ]
                                    ],
                                    "period": 300,
                                    "region": "us-east-1",
                                    "stat": "Sum",
                                    "title": "EFS Client connections",
                                    "yAxis": {
                                        "left": {
                                            "showUnits": false,
                                            "min": 0,
                                            "label": "#"
                                        }
                                    }
                                }
                            },
                            {
                                "type": "metric",
                                "width": 6,
                                "height": 6,
                                "properties": {
                                    "metrics": [
                                        [ { "expression": "SELECT SUM(StorageBytes) FROM SCHEMA(\"AWS/EFS\", FileSystemId,StorageClass) WHERE StorageClass = 'Total'", "id": "q1", "visible": false } ],
                                        [ { "expression": "q1/1048576", "label": "Total megabytes", "id": "e5" } ]
                                    ],
                                    "period": 300,
                                    "region": "us-east-1",
                                    "stat": "Sum",
                                    "title": "EFS Storage MBs",
                                    "yAxis": {
                                        "left": {
                                            "showUnits": false,
                                            "min": 0,
                                            "label": "MB"
                                        }
                                    }
                                }
                            },
                            {
                                "type": "metric",
                                "width": 6,
                                "height": 6,
                                "properties": {
                                    "metrics": [
                                        [ { "expression": "SELECT SUM(DatabaseConnections) FROM SCHEMA(\"AWS/RDS\")", "label": "Number of DB connections", "id": "q3" } ]
                                    ],
                                    "period": 300,
                                    "region": "us-east-1",
                                    "stat": "Sum",
                                    "title": "Number of database connections",
                                    "yAxis": {
                                        "left": {
                                            "showUnits": false,
                                            "min": 0,
                                            "label": "#"
                                        }
                                    }
                                }
                            },
                            {
                                "type": "metric",
                                "width": 6,
                                "height": 6,
                                "properties": {
                                    "metrics": [
                                        [ { "expression": "SELECT AVG(CPUUtilization) FROM SCHEMA(\"AWS/RDS\")", "label": "DB CPU utilization", "id": "q6" } ]
                                    ],
                                    "period": 300,
                                    "region": "us-east-1",
                                    "stat": "Sum",
                                    "title": "Database CPU utilization",
                                    "yAxis": {
                                        "left": {
                                            "showUnits": false,
                                            "min": 0,
                                            "max": 100,
                                            "label": "%"
                                        }
                                    }
                                }
                            },
                            {
                                "type": "metric",
                                "width": 6,
                                "height": 6,
                                "properties": {
                                    "metrics": [
                                        [ "AWS/RDS", "WriteIOPS" ],
                                        [ ".", "ReadIOPS" ]
                                    ],
                                    "period": 300,
                                    "region": "us-east-1",
                                    "stat": "Sum",
                                    "title": "Database IOPS",
                                    "yAxis": {
                                        "left": {
                                            "showUnits": false,
                                            "min": 0,
                                            "label": "IOPS"
                                        }
                                    }
                                }
                            }

                        ]
                    }
